/* COMMON */
body {
  color: #333;
  font-size: 16px;
  font-weight: 400;
  line-height: 1.4;
  font-family: 'Nanum Gothic', sans-serif;
  box-sizing: border-box;
  height: 3000px;
}

image {
  display: block;
}

a {
  text-decoration: none;
}

input {
  outline: none;
  border: none;
}

.inner {
  width: 1100px;
  margin: 0 auto;
  position: relative;
}

.btn {
  width: 130px;
  border: 2px solid #333;
  padding: 10px;
  border-radius: 4px;
  font-size: 16px;
  color: #333;
  font-weight: 700;
  text-align: center;
  transition: 0.4s;
  box-sizing: border-box;
}
.btn:hover {
  background-color: #333;
  color: #fff;
}
.btn.btn--brown {
  border: 2px solid #592b18;
  color: #592b18;
}
.btn.btn--brown:hover {
  color: #fff;
  background-color: #592b18;
}

/*#################### Header ####################*/
header > .inner {
  height: 120px;
}
header {
  width: 100%;
  background-color: #f6f5f0;
  position: fixed;
  border-bottom: 1px solid #c8c8c8;
  top: 0;
  z-index: 10;
  .inner {
    .logo {
      height: 75px;
      position: absolute;
      top: 0;
      bottom: 0;
      margin: auto;
    }
    .sub-menu {
      position: absolute;
      right: 0px;
      top: 10px;
      overflow: hidden;
      .menu {
        float: left;
        font-family: Arial, sans-serif;
        overflow: hidden;
        li {
          float: left;
          font-size: 12px;
          padding: 11px 16px;
          position: relative;
          a {
            color: #656565;
          }
          a:hover {
            color: #333;
            transition: 0.4s;
          }
        }
        li::before {
          content: '';
          width: 1px;
          height: 12px;
          background-color: #e5e5e5;
          position: absolute;
          left: 0;
        }
        li:first-child::before {
          display: none;
        }
      }
      .search {
        float: right;
        position: relative;
        cursor: pointer;
        input {
          width: 36px;
          height: 34px;
          font-size: 12px;
          border: 1px solid #ccc;
          border-radius: 8px;
          transition: 0.4s;
          border: 1px solid #ccc;
        }
        .material-icons {
          position: absolute;
          height: 24px;
          right: 10px;
          top: 0;
          bottom: 0;
          margin: auto;
          transition: 0.4s;
        }
      }
      // focused
      .search.focused {
        input {
          width: 190px;
          border-color: #669900;
        }
        .material-icons {
          opacity: 0;
        }
      }
    }
    .main-menu {
      position: absolute;
      width: 100%;
      overflow: hidden;
      right: 0;
      bottom: 0;
      font-family: Arial, sans-serif;
      .item {
        position: relative;
        padding: 10px 20px 34px 20px;
        margin: 0;
        font-size: 13px;
        float: left;
        left: 350px;
        .item__contents {
          position: fixed;
          width: 100%;
          top: 120px;
          left: 0;
          background-color: #2c2a29;
          display: none;
          z-index: 10;
          .contents__menu {
            overflow: hidden;
            padding: 20px 0;
            .inner > li {
              float: left;
            }
            .inner {
              li {
                width: 220px;
                h4 {
                  padding: 3px 0 12px 0;
                  color: #fff;
                  font-size: 14px;
                }
                ul {
                  padding: 0;
                  li {
                    padding: 5px 0;
                    color: #999;
                    font-size: 12px;
                    cursor: pointer;
                  }
                  li:hover {
                    color: #669900;
                  }
                }
              }
            }
          }
          .contents__texture {
            background-image: url(./images/main_menu_pattern.jpg);
            padding: 26px 0;
            font-size: 12px;
            .inner {
              h4 {
                color: #999;
                font-weight: 700;
              }
              p {
                color: #669900;
                margin: 4px 0px 14px;
              }
            }
          }
        }
      }
      .item:hover {
        background-color: #2c2a29;
        color: #669900;
        border-radius: 6px 6px 0px 0px;
        .item__contents {
          display: block;
        }
      }
    }
  }
  .badges {
    position: absolute;
    right: 20px;
    margin-top: 20px;
    .badge {
      img {
        border-radius: 12px;
        box-shadow: 5px 5px 10px rgba(0, 0, 0, 0.2);
      }
    }
  }
}

/*#################### VISUAL ####################*/
.visual {
  margin-top: 120px;
  background-image: url('./images/visual_bg.jpg');
  background-position: center;
  .inner {
    height: 646px;
    .title {
      position: absolute;
      top: 88px;
      left: -10px;
      text-align: center;
      opacity: 0;
      transition: 0.4s;
      img {
        display: block;
      }
      a {
        margin-top: 25px;
        display: inline-block;
      }
    }
    .cup1__image {
      position: absolute;
      top: 80px;
      right: -50px;
      opacity: 0;
    }
    .cup1__text {
      position: absolute;
      top: 50px;
      right: 180px;
      opacity: 0;
    }
    .cup2__image {
      position: absolute;
      top: 150px;
      right: 150px;
      opacity: 0;
    }
    .cup2__text {
      position: absolute;
      top: 320px;
      right: 420px;
      opacity: 0;
    }
    .spoon {
      position: absolute;
      bottom: 0;
      right: 400px;
      opacity: 0;
    }
  }
  .show .delay-0 {
    opacity: 1;
    transition: 0.8s;
    transition-delay: 0.4s;
  }
  .show .delay-1 {
    opacity: 1;
    transition: 0.8s;
    transition-delay: 1s;
  }
  .show .delay-2 {
    opacity: 1;
    transition: 0.8s;
    transition-delay: 1.6s;
  }
  .show .delay-3 {
    opacity: 1;
    transition: 0.8s;
    transition-delay: 2.2s;
  }
}
